import React, { Component } from 'react';
import './NewTodo.css';

var apiKey = "7bb18b-b475a9-ca1a2f-812109-55f3c2";
var api = "https://cse204.work/todos";

class NewTodo extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.addItem = this.addItem.bind(this);
    this.myRef = React.createRef();
  }

  handleKeyDown(e) {
    if (e.key === 'Enter') {
        this.addItem();

    }
  }

  addItem() {
    if (this.myRef.current.value === "") {
      alert("Please type in a ToDo");
    }
    else {
      console.log("adding item")
      // Code source: http://cse204.work/todo-api#create-info
      var newItem = document.getElementById("add-item").value;

      var data = {
          text: newItem
      };

      var xhttp = new XMLHttpRequest();

      xhttp.onreadystatechange = function () {
          if (this.readyState == 4 && this.status == 200) {
          var todo = JSON.parse(this.responseText);
          console.log(todo);
          listToDos();
          } 
          else if (this.readyState == 4) {
          console.log(this.responseText);

          }
      };
      xhttp.open("POST",api,true);

      xhttp.setRequestHeader("Content-type","application/json");
      xhttp.setRequestHeader("x-api-key",apiKey)
      xhttp.send(JSON.stringify(data));


    }

    document.getElementById("add-item").value = "";
    console.log("added");
  }


  render() {
    return (
      <div className="row">
        <input type="text" id="add-item" placeholder="Add To-do" onKeyDown={this.handleKeyDown} ref={this.myRef}></input>
        <i className="far fa-plus-circle fa-clickable" id="add-item-button" onClick={this.addItem}></i>
      </div>
    );
  }
}

export default NewTodo;
