    //     }
    // }

    // xhttp.open("DELETE",api + "/" + id, true);
    // xhttp.setRequestHeader("x-api-key",apiKey);
    // xhttp.send();

    // console.log("deleted");

  }

  render() {
    var checked;
    if (this.state.completed) {
      checked = "list-group-item list-group-item-action active";
    }
    else {
      checked = "list-group-item list-group-item-action";
    }
    // console.log(this);

    return (
      <li className={checked} onDoubleClick={this.checkItem} id={this.props.id}>
        {this.props.text}
        <div className='removeButton' onClick={this.removeItem}>
          <i className='far fa-trash' onClick={this.removeItem}></i>
        </div>
      </li>
    );
  }



}

export default Todo;
