import React, { Component } from 'react';
import './Todo.css';

class Todo extends Component {

  render() {
    var checked;
    if (this.props.completed) {
      checked = "active";
    }
    else {
      checked = "";
    }

    return (
      <li className='list-group-item list-group-item-action {checked}'>
        {this.props.text}
        <div className='removeButton'>
          <i className='far fa-trash'></i>
        </div>
      </li>
    );
  }
}

export default Todo;
