import React, { Component } from 'react';
import './NewTodo.css';

class NewTodo extends Component {
  constructor(props) {
    super(props);
    this.state = []
  }

  render() {
    return (
      <div className="row">
        <input type="text" id="add-item" placeholder="Add To-do" id={this.props.inputId}></input>
        <i className="far fa-plus-circle fa-clickable" id="add-item-button" onClick={this.addTodo}></i>
      </div>
    );
  }
}

export default NewTodo;
