import React, { Component } from 'react';
import './NewTodo.css';

class NewTodo extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.handleKeyDown = this.handleKeyDown.bind(this);
    this.addItem = this.addItem.bind(this);
  }

  handleKeyDown(e) {
    if (e.key === 'Enter') {
      this.addItem();
    }
  }

  render() {
    return (
      <div className="row">
        <input type="text" id="add-item" placeholder="Add To-do" onKeyDown={this.handleKeyDown}></input>
        <i className="far fa-plus-circle fa-clickable" id="add-item-button" onClick={this.addTodo}></i>
      </div>
    );
  }
}

export default NewTodo;
