import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

var apiKey = "7bb18b-b475a9-ca1a2f-812109-55f3c2";
var api = "https://cse204.work/todos";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {todos:[]};

  }

  addItem() {

  }
  render() {
    return (
        <div className="App">
          <div className="container-fluid">
            <div className="row">
                <div className="title">To-Do List</div>
            </div>

          <NewTodo />
          <ul className="list-group" id="ul">
            {this.state.todos.map((todo) =>
              <Todo key={todo.id} id={todo.id} text={todo.text} completed={todo.completed} created={todo.created_at} checkItem={this.checkItem}/>
            )}

          </ul>

        </div>
        </div>
    );
  }

  componentDidMount() {
    this.listToDos();

  }

  // checkItem(event) {
  //   console.log("checking item");
  //   var api_id = event.target.id;
  //   var checked = event.target.className;
  //   console.log(checked);


  //   var xhttp = new XMLHttpRequest();

  //   xhttp.onreadystatechange = function () {
  //       if (this.readyState === 4 && this.status === 200) {
  //       var todo = JSON.parse(this.responseText);
  //       console.log(todo.completed);

  //       } 
  //   };

  //   xhttp.open("PUT",api + "/" + api_id,true);

  //   xhttp.setRequestHeader("Content-type","application/json");
  //   xhttp.setRequestHeader("x-api-key",apiKey);
  //   xhttp.send(JSON.stringify(data));
  //   this.setState({completed:completed})

  //   console.log("checked")
  // }

  listToDos() {
    var xhttp = new XMLHttpRequest()
    var todos = [];

    xhttp.onreadystatechange = function () {
      if (this.readyState === 4 && this.status === 200) {
        todos = JSON.parse(this.responseText);
      }
    }

    xhttp.open("GET", api, false);
    xhttp.setRequestHeader("x-api-key", apiKey);
    xhttp.send();
    console.log(todos[0]);
    this.setState({ todos: todos });
  }



}



export default App;
