import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

var apiKey = "7bb18b-b475a9-ca1a2f-812109-55f3c2";
var api = "https://cse204.work/todos";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {todos:[]};
    this.addItem = this.addItem.bind(this);
    this.myRef = React.createRef();
  }



  render() {
    return (
        <div className="App">
          <div className="container-fluid">
            <div className="row">
                <div className="title">To-Do List</div>
            </div>

          <NewTodo text={this.text} onClick={this.onClick} onKeyDown={this.onKeyDown} ref={this.ref}/>
          <ul className="list-group" id="ul">
            {this.state.todos.map((todo) =>
              <Todo key={todo.id} id={todo.id} text={todo.text} completed={todo.completed} created={todo.created_at}/>
            )}

          </ul>

        </div>
        </div>
    );
  }

  componentDidMount() {
    this.listToDos();

  }

  handleKeyDown(e) {
    if (e.key === 'Enter') {
        this.addItem();

    }
  }

  addItem() {
    if (this.myRef.current.value === "") {
      alert("Please type in a ToDo");
    }
    else {
      var deez = this;
      console.log("adding item");
      console.log(this.props);
      var newItem = this.myRef.current.value;

      var data = {
        text:newItem
      }

      var xhttp = new XMLHttpRequest();

      xhttp.onreadystatechange = function () {
          if (this.readyState === 4 && this.status === 200) {
          var todo = JSON.parse(this.responseText);
          console.log(todo);
          deez.setState({todos:todo});

          } 
          else if (this.readyState === 4) {
          console.log(this.responseText);

          }
      };
      xhttp.open("POST",api,true);

      xhttp.setRequestHeader("Content-type","application/json");
      xhttp.setRequestHeader("x-api-key",apiKey)
      xhttp.send(JSON.stringify(data));


    }
  }

  listToDos() {
    var xhttp = new XMLHttpRequest()
    var todos = [];

    xhttp.onreadystatechange = function () {
      if (this.readyState === 4 && this.status === 200) {
        todos = JSON.parse(this.responseText);
      }
    }

    xhttp.open("GET", api, false);
    xhttp.setRequestHeader("x-api-key", apiKey);
    xhttp.send();
    console.log(todos[0]);
    this.setState({ todos: todos });
  }



}



export default App;
