import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

var apiKey = "7bb18b-b475a9-ca1a2f-812109-55f3c2";
var api = "https://cse204.work/todos";

class App extends Component {

// Adding event listeners
//document.getElementById("add-item-button").addEventListener("click",addItem);
//document.addEventListener("keydown",submit);


// Function that registers submit/ pressing enter key
submit() {
    if (event.which === 13 || event.keycode === 13) {
        addItem();
    }
}

// Function that sends newly added item to server
addItem() {
    if (document.getElementById("add-item").value == "") {
        alert("Please type in a To-Do");
    }
    else {
        console.log("adding item")
        // Code source: http://cse204.work/todo-api#create-info
        var newItem = document.getElementById("add-item").value;

        var data = {
            text: newItem
        };

        var xhttp = new XMLHttpRequest();

        xhttp.onreadystatechange = function () {
            if (this.readyState == 4 && this.status == 200) {
            var todo = JSON.parse(this.responseText);
            console.log(todo);
            listToDos();
            } 
            else if (this.readyState == 4) {
            console.log(this.responseText);

            }
        };
        xhttp.open("POST",api,true);

        xhttp.setRequestHeader("Content-type","application/json");
        xhttp.setRequestHeader("x-api-key",apiKey)
        xhttp.send(JSON.stringify(data));


    }

    document.getElementById("add-item").value = "";
    console.log("added");
    //listToDos();

}

removeItem() {
    event.stopImmediatePropagation();
    console.log("removing");
    var xhttp = new XMLHttpRequest();
    var api_id = event.target.getAttribute("id");

    xhttp.onreadystatechange = function() {
        if (this.readyState == 4 && this.status == 200) {
            console.log(api_id);
            listToDos();
        }
    }

    xhttp.open("DELETE",api + "/" + api_id, true);
    xhttp.setRequestHeader("x-api-key",apiKey);
    xhttp.send();

    console.log("deleted");
    //listToDos();
}

checkItem() {
    console.log("checking item");
    var api_id = event.target.getAttribute("data-id");
    var completed = event.target.classList.contains("active");
    console.log(completed);

    if (completed == false) {
        var data = {
            completed: true
        }
    }
    else {
        var data = {
            completed: false
        }
    }

    var xhttp = new XMLHttpRequest();

    xhttp.onreadystatechange = function () {
        if (this.readyState == 4 && this.status == 200) {
        var todo = JSON.parse(this.responseText);
        console.log(todo.completed);
        listToDos();
        } 
    };

    xhttp.open("PUT",api + "/" + api_id,true);

    xhttp.setRequestHeader("Content-type","application/json");
    xhttp.setRequestHeader("x-api-key",apiKey);
    xhttp.send(JSON.stringify(data));

    console.log("checked")
    //listToDos();
}

// Lists all the to do items on the server onto the webpage
listToDos() {
    // Clears list
    document.getElementById("ul").innerHTML = "";

    // From JS example "List All To Dos" on http://cse204.work/todo-api
    var xhttp = new XMLHttpRequest();
    xhttp.onreadystatechange = function() {
        if (this.readyState == 4 && this.status == 200) {
            var root = document.getElementsByClassName("list-group");
            root.innerHTML = '';
            todos = JSON.parse(this.responseText);
            console.log(todos);
            for (i=0;i<todos.length;i++) {
                console.log(todos[i]);
                var text = todos[i].text;
                var id = todos[i].id;
                if (todos[i].completed) {
                    $("<li class='list-group-item list-group-item-action active' data-id='" + id + "'>" + text + "<div class='removeButton'><i class='far fa-trash' id='" + id + "'></i></div></li>").appendTo(root);
                }
                else {
                    $("<li class='list-group-item list-group-item-action' data-id='" + id + "'>" + text + "<div class='removeButton'><i class='far fa-trash' id='" + id + "'></i></div></li>").appendTo(root);
                }
            }
            var myList = document.getElementsByClassName("list-group-item");
            for (var i=0;i<myList.length;i++) {
               myList[i].addEventListener("click",checkItem);
            }
            var myList = document.getElementsByClassName("removeButton");
            for (var i=0;i<myList.length;i++) {
                myList[i].addEventListener("click",removeItem);
            }
        }
    };


    xhttp.open("GET", api, true);
    xhttp.setRequestHeader("x-api-key",apiKey);
    xhttp.send();

}

  render() {
    return (
        <div className="App">
          <div className="container-fluid">
            <div class="row">
                <div class="title">To-Do List</div>
            </div>

          <NewTodo />
          <Todo />
        </div>
        </div>
    );
  }
}


export default App;
